172.source:
function ABPmarkChildNodesAsSkippedAfter(index) {
    if(typeof this.childNodes != "undefined"){
        for(var i = index ; i < this.childNodes.length; i++) {
           var child = this.childNodes[i];
           child.ABPmarkAsSkipped();
           child.ABPmarkChildNodesAsSkippedAfter(0);
        }
    }

}

