1746.source:
function ABPfailHard() {
    this.ABPreturn();
    this.ABPmarkChildNodesAsSkippedAfter(this.abp.i);
    this.submorphs[0].setTextString(this.abp.beta);
    this.submorphs[0].setTextColor(Color.red);
    // beta cut off
}

1750.source:
function markFinalDrawWith(points) {
    if(this.ABPhasChildNodes()) {
        for(var i = 0 ; i < this.childNodes.length; i++) {
            var child = this.childNodes[i];
            child.markFinalDrawWith(-points);
        }
    } else{
        if(this.abp.m == points) {
            this.setBorderColor(Color.green);
        }
    }
}

1804.source:
function buildChildNodes() {
    if(this.getLevel() >= this.getGameTree().getMovesCount()){
        return;
    }
    var that = this;
    this.childNodes = new Array();
    this.node.buildChildNodes();
    this.node.getChilds().forEach(function(node){
       var child = that.getGameTree().getNewNode();
       child.setNode(node);
       that.getGameTree().addNode(child);
        that.getGameTree().addMorph(child);
       child.buildChildNodes();
       child.setParent(that);
       that.childNodes.push(child);
   });
}

1816.source:
function getLevelWeight(level) {
       if (level == 0) {
         return 1;
       } else if (this.level == level - 1) {
             return this.childNodes.length;
        } else {
           var weight = 0;
             this.childNodes.forEach(function(ele) {
                 weight = weight + ele.getLevelWeight(level);
                                 });
          return weight;
       }
}

