2348.source:
function ABPmarkChildNodesAsSkippedAfter(index) {
    if(typeof this.childNodes != "undefined"){
        for(var i = index ; i < this.childNodes.length; i++) {
           var child = this.childNodes[i];
           child.ABPmarkAsSkipped();
           child.ABPmarkChildNodesAsSkippedAfter(0);
        }
    }

}

2368.source:
function showState() {
    if(typeof this.abp != "undefined") {
        var m = this.getStateMorph();
        m.setAlpha(this.abp.alpha);
        m.setBeta(this.abp.beta);
   }
}

2372.source:
function ABPfailHard() {
    this.ABPreturn();
    this.ABPmarkChildNodesAsSkippedAfter(this.abp.i);
    this.submorphs[0].setTextString(this.abp.beta);
    this.submorphs[0].setTextColor(Color.red);
    // beta cut off
}

2376.source:
function markFinalDrawWith(points) {
    if(this.ABPhasChildNodes()) {
        for(var i = 0 ; i < this.childNodes.length; i++) {
            var child = this.childNodes[i];
            child.markFinalDrawWith(-points);
        }
    } else{
        if(this.abp.m == points) {
            this.setBorderColor(Color.green);
        }
    }
}

2382.source:
function buildChildNodes() {
    if(this.getLevel() >= this.getGameTree().getMovesCount()){
        return;
    }
    var that = this;
    this.childNodes = new Array();
    this.node.buildChildNodes();
    this.node.getChilds().forEach(function(node){
       var child = that.getGameTree().getNewNode();
       child.setNode(node);
       that.getGameTree().addNode(child);
        that.getGameTree().addMorph(child);
       child.buildChildNodes();
       child.setParent(that);
       that.childNodes.push(child);
   });
}

2394.source:
function getLevelWeight(level) {
       if (level == 0) {
         return 1;
       } else if (this.level == level - 1) {
             return this.childNodes.length;
        } else {
           var weight = 0;
             this.childNodes.forEach(function(ele) {
                 weight = weight + ele.getLevelWeight(level);
                                 });
          return weight;
       }
}

2406.source:
function setParent(parent) {
    this.parent = parent;
    this.level = parent.getLevel() + 1;
}

2422.source:
function ABPreturn() {
    this.blink();
    this.ABPshowValue();
    if(this.abp.origAlpha == this.abp.alpha){
        // "alpha return" therefore the I have better draws than this
       this.submorphs[0].setTextColor(Color.blue);
    } 
    setTimeout(function(){
       this.ABPmarkAsDone();
       this.getGameTree().ABPreturn(this.abp.parentCallback);
    }.bind(this), 300); 
}

