5097.source:
function ABPcontinue() {
    this.showState();

    if( this.ABPhasChildNodes() == false ) {
        this.abp.m = this.node.state.value();
        this.abp.alpha = this.abp.m;
    }
    if (this.ABPhasChildNodes() == false || this.abp.i >= this.childNodes.length){
        this.ABPreturn();
    } else {

      var child = this.childNodes[this.abp.i];

      var callbackForChild = function() {
        this.abp.i++;
        var t = -child.ABPgetValue();

       if(t > this.abp.m) {
            this.abp.m = t;
            this.abp.alpha = this.abp.m;
        }

        if(this.abp.isActive == true) {
         // use additional recursive exit condition for ABP optimization
            if (t >= this.abp.beta){
                // this state is better for me than the best draw of my oppenent,   therefore he would never chose this branch
              this.ABPfailHard();
                return;
            }
        }

         this.ABPcontinue();
       }.bind(this);

      child.ABPstart(this.abp.isActive, - this.abp.beta, - this.abp.m, callbackForChild);

    }
}

