7984.source:
function init() {
    this.levels = new Array();
    this.gameTree = new ABPGameTree();
    this.clean();
    this.abp.done = false;
    this.nextStep = undefined;

  }

7988.source:
function setState(state) {
    this.resize();
    this.init();
    this.gameTree.buildWithState(state);
    this.buildSkeleton();
    this.drawSkeleton();
}

7992.source:
function drawSkeleton() {
       debugger;
     var extent = this.getExtent();
     for (var i = 0; i < this.levels.length; i++) {
         var distance = extent.x/(this.levels[i].length + 1);
             for (var j = 0; j < this.levels[i].length; j++){
                 var node = this.levels[i][j];
                 this.arrangeNode(node, distance, i, j);
                 this.connect(node, node.getParent());
             }
     };
}

8004.source:
function buildSkeleton() {
   this.rootNode = this.getNewNode();
   this.rootNode.setNode(this.gameTree.rootNode);
   this.addMorph(this.rootNode);
   this.addNode(this.rootNode);
   this.rootNode.buildChildNodes();
}

8012.source:
function addNode(node) {
       var nodeLevel = node.getLevel();
   if (typeof this.levels[nodeLevel] == "undefined") {
             this.levels[nodeLevel] = new Array();
    }
    this.levels[nodeLevel].push(node);
}

8016.source:
function arrangeNode(node, distance, level, nr) {
    var magicPaddingTop = 60, magicPaddingLeft = 45;
    node.setPosition(pt(magicPaddingLeft + distance*(nr+1)-node.getExtent().x/2,magicPaddingTop + level*50));
}

8020.source:
function connect(child, father) {
   if (typeof father == "undefined") {
            return;
    }
    var nodeFatherCenter = father.getCenter();
    var nodeChildCenter = child.getCenter();
    var delta = nodeChildCenter.subPt(nodeFatherCenter).addPt(pt(0,-14));
   var edge = this.getNewEdge();
     this.addMorph(edge);
    edge.setPosition(nodeFatherCenter);
    edge.shape.setVertices([pt(0,14),delta]);
}

8040.source:
function ABPinit() {
    var that = this;
    this.abp = {
                 "isActive" : true,
                 "alpha" : -10000000000000,
                "beta" :   10000000000000,
                "done" : false
           };
    this.nextStep = function(){
        this.rootNode.ABPstart( this.abp.isActive, this.abp.alpha, this.abp.beta, function(){
           that.ABPdone();
         });
     }.bind(this);

}

8056.source:
function resize() {
    this.setPosition(pt(-70,40)); // sophisticated numbers
    var sizeOfOwner = this.owner.getExtent();
    var mySize = sizeOfOwner.addPt(pt(-20,-96));
    this.setExtent(mySize);

}

8060.source:
function NegaMaxInit() {
    var that = this;
    this.abp = {
                 "isActive" : false,
                 "alpha" : -10000000000000,
                "beta" :   10000000000000,
                "nextStep" : null,
                "done" : false
           };
    this.nextStep = function(){
        this.rootNode.ABPstart( this.abp.isActive, this.abp.alpha, this.abp.beta, function(){
           that.ABPdone();
         });
     }.bind(this);

}

8064.source:
function ABPstop() {
    if( typeof this.autoRunInterval != "undefined") {
        clearInterval(this.autoRunInterval);
    }
}

8068.source:
function autorun() {
    var that = this;

     if( typeof this.nextStep == "undefined") {
         this.initAlgorithm();
     }

    this.autoRunInterval = setInterval(function(){ if(that.abp.done){ clearInterval(this.autoRunInterval);} else { that.makeStep(); } }.bind(this), 1000);
}

8072.source:
function makeStep() {
     if( typeof this.nextStep == "undefined") {
         this.initAlgorithm();
     }
     if(this.abp.done == false) {
        this.nextStep();
    }
}

8076.source:
function initAlgorithm() {
     if( $morph("chkbUseABP").isChecked() ) {
         this.ABPinit();
     } else {
         this.NegaMaxInit();
     }

}

